import React, { useState } from "react";
import { generateCode, isValidShortcode, logEvent, urlMap, statsMap } from "./utils";

export default function ShortenForm() {
  const [url, setUrl] = useState("");
  const [customCode, setCustomCode] = useState("");
  const [validity, setValidity] = useState("");
  const [shortUrl, setShortUrl] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    let code = customCode || generateCode();
    if (!isValidShortcode(code)) return alert("Invalid shortcode");
    if (urlMap.has(code)) return alert("Shortcode already exists");

    const expiry = Date.now() + (validity ? validity * 60000 : 1800000);
    urlMap.set(code, { url, expiry });
    statsMap.set(code, 0);
    logEvent(`Created short URL: ${code}`);

    setShortUrl(`${window.location.origin}/${code}`);
  };

  return (
    <form onSubmit={handleSubmit} className="form">
      <input
        type="text"
        placeholder="Enter long URL"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        className="input"
        required
      />
      <input
        type="text"
        placeholder="Custom shortcode (optional)"
        value={customCode}
        onChange={(e) => setCustomCode(e.target.value)}
        className="input"
      />
      <input
        type="number"
        placeholder="Validity in minutes (optional)"
        value={validity}
        onChange={(e) => setValidity(e.target.value)}
        className="input"
      />
      <button type="submit" className="button">
        Shorten
      </button>
      {shortUrl && (
        <p className="short-url">
          Shortened URL: <a href={shortUrl}>{shortUrl}</a>
        </p>
      )}
    </form>
  );
}
